import React from "react";
import Jumbotron from "./components/Jumbotron";
import Nav from "./components/Nav";
import Container from "./components/Container";
import Row from "./components/Row";
import ImageCard from "./components/ImageCard";
import Footer from "./components/Footer";


var images = [
    {
        name: 'hello',
        id: 1,
        url: "https://images.unsplash.com/photo-1558981822-0c0c5b070026?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=634&q=80",
        clicked: false
    },
    {
        name: 'hello 2',
        id: 2,
        url: "https://images.unsplash.com/photo-1558981822-0c0c5b070026?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=634&q=80",
        clicked: false
    },
    {
        name: 'hello 3',
        id: 3,
        url: "https://images.unsplash.com/photo-1558981822-0c0c5b070026?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=634&q=80",
        clicked: false
    }
]

class App extends React.Component {

    state = {
        message: 'Click any image to begin',
        images: images
    }


    handleImageClick = (id) => {
        
        console.log(id);
        
        var images = this.state.images

        images.sort(() => Math.random() - 0.5);


        this.setState({
            message: 'I was clicked!. Thanks pal!',
            images: images
        })
    }



    // react component lifecycle method
    render(){
        return(
            //fragment container
            <>
                <Nav></Nav>
                <Jumbotron></Jumbotron>
                <Container>

                <h1>{this.state.message}</h1>
        

                    <div className="col-3">
                    {this.state.images.map(each => {
                        return <div key={each.id}>

                        <h1>{each.name}</h1>
                        <img onClick={() => this.handleImageClick(each.id)} src={each.url} alt=""/>
                        </div>
                    })}
                    </div>
                </Container>
                
            </>
        )
    }
}

export default App;
